mapper.select:
select a.id, a.leave_type leaveType, a.reason, a.attachment, a.organization, a.start_date startDate, a.end_date endDate, a.back_date backDate, a.plan_days planDays, a.act_days actDays, a.apply_user_id applyUserId, a.apply_user_name applyUserName, a.apply_user_no applyUserNo, a.apply_user_tel applyUserTel, a.apply_user_dept_id applyUserDeptId, a.`status`, a.audit_user auditUser, a.current_audit_user_id currentAuditUserId, a.create_time createTime, a.update_time updateTime, a.is_delete isDelete, a.delete_time deleteTime, b.`name` currentAuditUserName, c.`name` instituteName, e.`name` leaveTypeName from biu_teacher_leave_info a left join biu_user b on a.current_audit_user_id=b.id left join biu_institute c on b.institute_id=c.id left join biu_dict_info e on a.leave_type=e.id
a.is_delete=2
and a.apply_user_id=#{userId}
and a.`status` in
#{status}
and a.create_time >= #{startDate}
and a.create_time <= #{endDate}
order by a.create_time desc

select a.id, a.leave_type leaveType, a.reason, a.attachment, a.organization, a.start_date startDate, a.end_date endDate, a.back_date backDate, a.plan_days planDays, a.act_days actDays, a.apply_user_id applyUserId, a.apply_user_name applyUserName, a.apply_user_no applyUserNo, a.apply_user_tel applyUserTel, a.apply_user_dept_id applyUserDeptId, case d.is_pass when 1 then 2 when 2 then 3 else 1 end status, a.audit_user auditUser, a.current_audit_user_id currentAuditUserId, a.create_time createTime, a.update_time updateTime, a.is_delete isDelete, a.delete_time deleteTime, b.`name` currentAuditUserName, c.`name` instituteName, e.`name` leaveTypeName from biu_teacher_leave_info a left join biu_user b on a.current_audit_user_id=b.id left join biu_institute c on b.institute_id=c.id left join biu_teacher_leave_audit d on a.id=d.leave_id left join biu_dict_info e on a.leave_type=e.id
a.is_delete=2
and d.user_id=#{userId}
and d.is_pass in
#{status}
and d.is_pass is null
and a.create_time >= #{startDate}
and a.create_time <= #{endDate}
order by a.create_time desc
